    public void test_example() {

        try {
            com.danielpark.httpconnection.util.FieldNamingPolicy.setFieldNamingType(FieldNamingType.LOWER_CAMER_CASE);

            final Object jsonResponse = parseResponse(jsonString);
            com.danielpark.httpconnection.util.FieldNamingPolicy.setFieldNamingType(FieldNamingType.IDENTITY);
            final Object jsonResponse2 = parseResponse(jsonString2);

            if (jsonResponse instanceof JsonObject){
                Log.d(TAG, "JsonObject : " + jsonResponse.toString());
            }
            if (jsonResponse2 instanceof JSONArray) {
                Log.d(TAG, "JsonArray : "  + jsonResponse2.toString());
            }
        } catch (JSONException e) {
            e.printStackTrace();
        }
    }

    /**
     * Returns Object of type {@link JsonObject}, {@link JSONArray}, String, Boolean, Integer, Long,
     * Double or {@link JSONObject#NULL}, see {@link org.json.JSONTokener#nextValue()}
     *
     * @return Object parsedResponse
     * @throws org.json.JSONException exception if thrown while parsing JSON
     */
    private Object parseResponse(String jsonString) throws JSONException {
        Object result = null;
        //trim the string to prevent start with blank, and test if the string is valid JSON, because the parser don't do this :(. If JSON is not valid this will return null
        if (jsonString != null) {
            jsonString = jsonString.trim();
            // Check if the string is an JsonObject style {} or JSONArray style []
            // If not we consider this as a string
            if ((jsonString.startsWith("{") && jsonString.endsWith("}"))
                    || jsonString.startsWith("[") && jsonString.endsWith("]")) {
//                result = new JSONTokener(jsonString).nextValue();
                result = new CustomJsonTokenizer(jsonString).nextValue();
            }
            // Check if this is a String "my String value" and remove quote
            // Other value type (numerical, boolean) should be without quote
            else if (jsonString.startsWith("\"") && jsonString.endsWith("\"")) {
                result = jsonString.substring(1, jsonString.length() - 1);
            }
        }
        if (result == null) {
            result = jsonString;
        }
        return result;
    }



